contracts/casino/CasinoTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract CasinoTreasury is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    mapping(address => bool) public authorizedGames;
    bool public paused;

    event FundsDeposited(address indexed token, uint256 amount);
    event FundsWithdrawn(address indexed token, uint256 amount, address to);
    event GameAuthorized(address indexed game);
    event GameRevoked(address indexed game);
    event Paused();
    event Unpaused();

    modifier onlyAuthorized() {
        require(authorizedGames[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Treasury paused");
        _;
    }

    constructor(address _owner) Ownable(_owner) {
        paused = false;
    }

    function authorizeGame(address game) external onlyOwner {
        authorizedGames[game] = true;
        emit GameAuthorized(game);
    }

    function revokeGame(address game) external onlyOwner {
        authorizedGames[game] = false;
        emit GameRevoked(game);
    }

    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }

    function unpause() external onlyOwner {
        paused = false;
        emit Unpaused();
    }

    function deposit(address token, uint256 amount) external payable whenNotPaused {
        if (token == address(0)) {
            // Native MON
            require(msg.value == amount, "Amount mismatch");
            emit FundsDeposited(address(0), amount);
        } else {
            // ERC20 token
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
            emit FundsDeposited(token, amount);
        }
    }

    function withdraw(address token, uint256 amount, address to) external onlyOwner nonReentrant {
        require(to != address(0), "Invalid recipient");
        if (token == address(0)) {
            // Native MON
            (bool success, ) = payable(to).call{value: amount}("");
            require(success, "Transfer failed");
            emit FundsWithdrawn(address(0), amount, to);
        } else {
            // ERC20 token
            IERC20(token).safeTransfer(to, amount);
            emit FundsWithdrawn(token, amount, to);
        }
    }

    function transferToGame(address token, address game, uint256 amount) external onlyAuthorized whenNotPaused {
        if (token == address(0)) {
            // Native MON
            (bool success, ) = payable(game).call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            // ERC20 token
            IERC20(token).safeTransfer(game, amount);
        }
    }

    function payOut(address token, address recipient, uint256 amount) external onlyAuthorized whenNotPaused {
        require(recipient != address(0), "Invalid recipient");
        if (token == address(0)) {
            // Native MON
            require(address(this).balance >= amount, "Insufficient treasury balance");
            (bool success, ) = payable(recipient).call{value: amount}("");
            require(success, "Payout failed");
        } else {
            // ERC20 token
            IERC20(token).safeTransfer(recipient, amount);
        }
    }

    function getBalance(address token) external view returns (uint256) {
        if (token == address(0)) {
            // Native MON
            return address(this).balance;
        } else {
            // ERC20 token
            return IERC20(token).balanceOf(address(this));
        }
    }

    // Allow contract to receive native MON
    receive() external payable {
        emit FundsDeposited(address(0), msg.value);
    }
}

contracts/casino/CoinFlip.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "../interfaces/IPythVRF.sol";
import "./libraries/CasinoMath.sol";
import "./CasinoTreasury.sol";

/**
 * @title CoinFlip
 * @dev Mission X compliant, VRF-powered arcade coin flip game
 * Features: Streak multipliers, cash-out strategy, multiplayer prize pools, VRF-based cosmetic loot
 */
contract CoinFlip is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ================= CONFIG =================

    struct GameConfig {
        address treasury;
        uint256 minPlay;
        uint256 maxPlay;
        uint16 houseEdgeBps;
        bool paused;
    }

    GameConfig public config;
    IPythVRF public pythVRF;
    address public token; // address(0) = native MON

    uint256 public gameCounter;
    uint256 public prizePool;

    // ================= GAME =================

    struct PendingGame {
        address player;
        uint256 amount;
        uint8 choice;
        uint8 streak;
        uint256 multiplierBps;
        bool cashOut;
    }

    struct GameResult {
        address player;
        uint256 gameId;
        uint256 result;
        uint256 reward;
        uint8 streak;
        uint256 lootRarity;
    }

    mapping(uint256 => PendingGame) public pendingGames;
    mapping(bytes32 => uint256) public vrfRequestToGame;
    mapping(uint256 => GameResult) public games;

    // ================= EVENTS =================

    event GameStarted(uint256 indexed gameId, address indexed player);
    event GameSettled(
        uint256 indexed gameId,
        uint256 result,
        uint256 reward,
        uint8 streak,
        uint256 lootRarity
    );

    // ================= CONSTRUCTOR =================

    constructor(
        address _owner,
        address _token,
        address _pythVRF
    ) Ownable(_owner) {
        token = _token;
        pythVRF = IPythVRF(_pythVRF);
    }

    function initialize(
        address treasury,
        uint256 minPlay,
        uint256 maxPlay,
        uint16 houseEdgeBps
    ) external onlyOwner {
        config = GameConfig({
            treasury: treasury,
            minPlay: minPlay,
            maxPlay: maxPlay,
            houseEdgeBps: houseEdgeBps,
            paused: false
        });
    }

    // ================= PLAY =================

    function play(uint256 amount, uint8 choice, bool cashOut)
        external
        payable
        nonReentrant
    {
        require(!config.paused, "Paused");
        require(choice <= 1, "Invalid choice");
        CasinoMath.validateBet(amount, config.minPlay, config.maxPlay);

        if (token == address(0)) {
            require(msg.value == amount, "Amount mismatch");
            prizePool += amount / 10; // multiplayer pool
            (bool ok, ) = payable(config.treasury).call{value: amount}("");
            require(ok, "Transfer failed");
        } else {
            prizePool += amount / 10;
            IERC20(token).safeTransferFrom(
                msg.sender,
                config.treasury,
                amount
            );
        }

        uint256 gameId = ++gameCounter;

        pendingGames[gameId] = PendingGame({
            player: msg.sender,
            amount: amount,
            choice: choice,
            streak: 0,
            multiplierBps: 19500,
            cashOut: cashOut
        });

        // Request VRF with seed
        // Mission X: No seed - VRF generates its own entropy
        bytes32 requestId = pythVRF.requestRandomness();
        vrfRequestToGame[requestId] = gameId;

        emit GameStarted(gameId, msg.sender);
    }

    // ================= VRF CALLBACK =================

    /**
     * @dev Mission X: Push-based callback - VRF calls this with randomness
     * This is the proper Mission X pattern - VRF pushes randomness, contract doesn't pull
     */
    function fulfillRandomness(bytes32 requestId, uint256 randomness) external {
        // Verify caller is VRF provider
        require(msg.sender == address(pythVRF), "Only VRF");

        uint256 gameId = vrfRequestToGame[requestId];
        PendingGame storage g = pendingGames[gameId];
        require(g.player != address(0), "Invalid game");

        uint256 result = randomness % 2;
        uint256 reward = 0;
        uint256 lootRarity = (randomness % 1000); // cosmetic loot

        if (g.choice == result) {
            g.streak += 1;
            g.multiplierBps += 500; // illusion of skill

            reward = CasinoMath.calculatePayout(
                g.amount,
                g.multiplierBps,
                config.houseEdgeBps
            );

            if (g.cashOut || g.streak >= 5) {
                CasinoTreasury(payable(config.treasury))
                    .payOut(token, g.player, reward);
            }
        } else {
            g.streak = 0;
        }

        // Multiplayer pool bonus
        if (lootRarity < 5 && prizePool > 0) {
            uint256 bonus = prizePool / 10;
            prizePool -= bonus;
            reward += bonus;

            CasinoTreasury(payable(config.treasury))
                .payOut(token, g.player, bonus);
        }

        games[gameId] = GameResult({
            player: g.player,
            gameId: gameId,
            result: result,
            reward: reward,
            streak: g.streak,
            lootRarity: lootRarity
        });

        delete pendingGames[gameId];
        delete vrfRequestToGame[requestId];

        emit GameSettled(
            gameId,
            result,
            reward,
            g.streak,
            lootRarity
        );
    }

    // ================= VIEW FUNCTIONS =================

    function getGame(uint256 gameId) external view returns (GameResult memory) {
        return games[gameId];
    }

    function getPendingGame(uint256 gameId) external view returns (PendingGame memory) {
        return pendingGames[gameId];
    }

    function getPrizePool() external view returns (uint256) {
        return prizePool;
    }

    // ================= ADMIN =================

    function pause() external onlyOwner {
        config.paused = true;
    }

    function unpause() external onlyOwner {
        config.paused = false;
    }

    function setPythVRF(address _pythVRF) external onlyOwner {
        pythVRF = IPythVRF(_pythVRF);
    }
}

contracts/casino/Crash.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "../interfaces/IPythVRF.sol";
import "./libraries/CasinoMath.sol";
import "./CasinoTreasury.sol";

/**
 * @title Crash
 * @dev Mission X compliant, VRF-powered arcade crash game
 * Features: Skill-based cash-out timing, VRF-determined crash point, multiplier system
 */
contract Crash is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct GameConfig {
        address treasury;
        uint256 minPlay;
        uint256 maxPlay;
        uint16 houseEdgeBps;
        bool paused;
    }

    GameConfig public config;
    IPythVRF public pythVRF;
    address public token; // address(0) = native MON

    uint256 public gameCounter;
    uint256 public prizePool;

    struct PendingGame {
        address player;
        uint256 amount;
        uint256 autoCashout; // Target multiplier (in bps, e.g., 20000 = 2x)
        bool cashedOut;
    }

    struct GameResult {
        address player;
        uint256 gameId;
        uint256 crashPoint; // Multiplier where game crashed (in bps)
        uint256 cashoutPoint; // Multiplier where player cashed out (in bps)
        uint256 reward;
        uint256 lootRarity;
    }

    mapping(uint256 => PendingGame) public pendingGames;
    mapping(bytes32 => uint256) public vrfRequestToGame;
    mapping(uint256 => bytes32) public gameToVrfRequest; // gameId => requestId
    mapping(uint256 => GameResult) public games;

    event GameStarted(uint256 indexed gameId, address indexed player);
    event Cashout(uint256 indexed gameId, uint256 multiplier, uint256 payout);
    event GameSettled(
        uint256 indexed gameId,
        uint256 crashPoint,
        uint256 cashoutPoint,
        uint256 reward,
        uint256 lootRarity
    );

    constructor(
        address _owner,
        address _token,
        address _pythVRF
    ) Ownable(_owner) {
        token = _token;
        pythVRF = IPythVRF(_pythVRF);
    }

    function initialize(
        address treasury,
        uint256 minPlay,
        uint256 maxPlay,
        uint16 houseEdgeBps
    ) external onlyOwner {
        config = GameConfig({
            treasury: treasury,
            minPlay: minPlay,
            maxPlay: maxPlay,
            houseEdgeBps: houseEdgeBps,
            paused: false
        });
    }

    function placeBet(uint256 amount, uint256 autoCashout) external payable nonReentrant {
        require(!config.paused, "Paused");
        require(autoCashout >= 10000 && autoCashout <= 100000, "Invalid cashout"); // 1x to 10x
        CasinoMath.validateBet(amount, config.minPlay, config.maxPlay);

        if (token == address(0)) {
            require(msg.value == amount, "Amount mismatch");
            prizePool += amount / 10; // multiplayer pool
            (bool ok, ) = payable(config.treasury).call{value: amount}("");
            require(ok, "Transfer failed");
        } else {
            prizePool += amount / 10;
            IERC20(token).safeTransferFrom(
                msg.sender,
                config.treasury,
                amount
            );
        }

        uint256 gameId = ++gameCounter;

        pendingGames[gameId] = PendingGame({
            player: msg.sender,
            amount: amount,
            autoCashout: autoCashout,
            cashedOut: false
        });

        // Mission X: No seed - VRF generates its own entropy
        bytes32 requestId = pythVRF.requestRandomness();
        vrfRequestToGame[requestId] = gameId;
        gameToVrfRequest[gameId] = requestId;

        emit GameStarted(gameId, msg.sender);
    }

    function cashout(uint256 gameId) external nonReentrant {
        PendingGame storage g = pendingGames[gameId];
        require(g.player == msg.sender, "Not your game");
        require(!g.cashedOut, "Already cashed out");

        // Get current game state
        bytes32 requestId = gameToVrfRequest[gameId];
        require(requestId != bytes32(0), "Game not found");

        // Mission X: Get randomness from VRF (for manual cashout before auto-fulfill)
        (uint256 randomness, , bool fulfilled) = pythVRF.getRandomness(requestId);
        require(fulfilled, "VRF not fulfilled");

        // Calculate crash point from VRF
        // Crash point is between 10000 (1x) and 100000 (10x) in bps
        uint256 crashPoint = 10000 + ((randomness % 90000)); // 1x to 10x

        // Player can cash out if they beat the crash point (skill-based timing)
        if (g.autoCashout < crashPoint) {
            uint256 reward = CasinoMath.calculatePayout(
                g.amount,
                g.autoCashout,
                config.houseEdgeBps
            );

            g.cashedOut = true;

            CasinoTreasury(payable(config.treasury))
                .payOut(token, g.player, reward);

            uint256 lootRarity = (randomness % 1000);

            games[gameId] = GameResult({
                player: g.player,
                gameId: gameId,
                crashPoint: crashPoint,
                cashoutPoint: g.autoCashout,
                reward: reward,
                lootRarity: lootRarity
            });

            emit Cashout(gameId, g.autoCashout, reward);
            emit GameSettled(
                gameId,
                crashPoint,
                g.autoCashout,
                reward,
                lootRarity
            );
        } else {
            // Crashed before cashout
            g.cashedOut = true;
            uint256 lootRarity = (randomness % 1000);

            games[gameId] = GameResult({
                player: g.player,
                gameId: gameId,
                crashPoint: crashPoint,
                cashoutPoint: 0,
                reward: 0,
                lootRarity: lootRarity
            });

            emit GameSettled(
                gameId,
                crashPoint,
                0,
                0,
                lootRarity
            );
        }

        delete pendingGames[gameId];
        delete vrfRequestToGame[requestId];
        delete gameToVrfRequest[gameId];
    }

    /**
     * @dev Mission X: Push-based callback - VRF calls this with randomness
     */
    function fulfillRandomness(bytes32 requestId, uint256 randomness) external {
        require(msg.sender == address(pythVRF), "Only VRF");

        uint256 gameId = vrfRequestToGame[requestId];
        PendingGame storage g = pendingGames[gameId];
        require(g.player != address(0), "Invalid game");

        // Calculate crash point
        uint256 crashPoint = 10000 + ((randomness % 90000)); // 1x to 10x

        uint256 reward = 0;
        uint256 lootRarity = (randomness % 1000);

        // Auto-cashout if set
        if (g.autoCashout < crashPoint && !g.cashedOut) {
            reward = CasinoMath.calculatePayout(
                g.amount,
                g.autoCashout,
                config.houseEdgeBps
            );

            g.cashedOut = true;

            CasinoTreasury(payable(config.treasury))
                .payOut(token, g.player, reward);
        }

        games[gameId] = GameResult({
            player: g.player,
            gameId: gameId,
            crashPoint: crashPoint,
            cashoutPoint: g.cashedOut ? g.autoCashout : 0,
            reward: reward,
            lootRarity: lootRarity
        });

        delete pendingGames[gameId];
        delete vrfRequestToGame[requestId];
        delete gameToVrfRequest[gameId];
        delete gameToVrfRequest[gameId];

        emit GameSettled(
            gameId,
            crashPoint,
            g.cashedOut ? g.autoCashout : 0,
            reward,
            lootRarity
        );
    }

    function getGame(uint256 gameId) external view returns (GameResult memory) {
        return games[gameId];
    }

    function getPendingGame(uint256 gameId) external view returns (PendingGame memory) {
        return pendingGames[gameId];
    }

    function getPrizePool() external view returns (uint256) {
        return prizePool;
    }

    function pause() external onlyOwner {
        config.paused = true;
    }

    function unpause() external onlyOwner {
        config.paused = false;
    }

    function setPythVRF(address _pythVRF) external onlyOwner {
        pythVRF = IPythVRF(_pythVRF);
    }
}

