// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "../interfaces/IPythVRF.sol";
import "./libraries/CasinoMath.sol";
import "./CasinoTreasury.sol";

/**
 * @title Dice
 * @dev Mission X compliant, VRF-powered arcade dice game
 * Features: Streak multipliers, probability-based payouts, VRF-based cosmetic rewards
 */
contract Dice is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct GameConfig {
        address treasury;
        uint256 minPlay;
        uint256 maxPlay;
        uint16 houseEdgeBps;
        bool paused;
    }

    GameConfig public config;
    IPythVRF public pythVRF;
    address public token; // address(0) = native MON

    uint256 public gameCounter;
    uint256 public prizePool;

    struct PendingGame {
        address player;
        uint256 amount;
        uint8 target;
        bool rollUnder;
        uint8 streak;
        uint256 multiplierBps;
    }

    struct GameResult {
        address player;
        uint256 gameId;
        uint256 roll;
        uint256 reward;
        uint8 streak;
        uint256 lootRarity;
    }

    mapping(uint256 => PendingGame) public pendingGames;
    mapping(bytes32 => uint256) public vrfRequestToGame;
    mapping(uint256 => GameResult) public games;

    event GameStarted(uint256 indexed gameId, address indexed player);
    event GameSettled(
        uint256 indexed gameId,
        uint256 roll,
        uint256 reward,
        uint8 streak,
        uint256 lootRarity
    );

    constructor(
        address _owner,
        address _token,
        address _pythVRF
    ) Ownable(_owner) {
        token = _token;
        pythVRF = IPythVRF(_pythVRF);
    }

    function initialize(
        address treasury,
        uint256 minPlay,
        uint256 maxPlay,
        uint16 houseEdgeBps
    ) external onlyOwner {
        config = GameConfig({
            treasury: treasury,
            minPlay: minPlay,
            maxPlay: maxPlay,
            houseEdgeBps: houseEdgeBps,
            paused: false
        });
    }

    function play(uint256 amount, uint8 target, bool rollUnder)
        external
        payable
        nonReentrant
    {
        require(!config.paused, "Paused");
        require(target > 0 && target < 100, "Invalid target");
        CasinoMath.validateBet(amount, config.minPlay, config.maxPlay);

        if (token == address(0)) {
            require(msg.value == amount, "Amount mismatch");
            prizePool += amount / 10; // multiplayer pool
            (bool ok, ) = payable(config.treasury).call{value: amount}("");
            require(ok, "Transfer failed");
        } else {
            prizePool += amount / 10;
            IERC20(token).safeTransferFrom(
                msg.sender,
                config.treasury,
                amount
            );
        }

        uint256 gameId = ++gameCounter;

        pendingGames[gameId] = PendingGame({
            player: msg.sender,
            amount: amount,
            target: target,
            rollUnder: rollUnder,
            streak: 0,
            multiplierBps: 0 // Will be calculated based on probability
        });

        // Mission X: No seed - VRF generates its own entropy
        bytes32 requestId = pythVRF.requestRandomness();
        vrfRequestToGame[requestId] = gameId;

        emit GameStarted(gameId, msg.sender);
    }

    /**
     * @dev Mission X: Push-based callback - VRF calls this with randomness
     */
    function fulfillRandomness(bytes32 requestId, uint256 randomness) external {
        require(msg.sender == address(pythVRF), "Only VRF");

        uint256 gameId = vrfRequestToGame[requestId];
        PendingGame storage g = pendingGames[gameId];
        require(g.player != address(0), "Invalid game");

        uint256 roll = (randomness % 100) + 1; // 1-100
        uint256 reward = 0;
        uint256 lootRarity = (randomness % 1000); // cosmetic loot

        bool won = g.rollUnder ? (roll < g.target) : (roll > g.target);

        if (won) {
            g.streak += 1;

            // Calculate probability-based multiplier
            uint256 probability = g.rollUnder ? (g.target - 1) : (100 - g.target);
            require(probability >= 5, "Too risky"); // Mission X: Prevent treasury drain

            uint256 baseMultiplierBps = (10000 * 10000) / probability;
            // Mission X: Cap multiplier to prevent extreme payouts
            if (baseMultiplierBps > 50000) {
                baseMultiplierBps = 50000; // Max 5x base multiplier
            }

            // Streak bonus (illusion of skill)
            g.multiplierBps = baseMultiplierBps + (g.streak * 500); // +500 bps per streak

            reward = CasinoMath.calculatePayout(
                g.amount,
                g.multiplierBps,
                config.houseEdgeBps
            );

            CasinoTreasury(payable(config.treasury))
                .payOut(token, g.player, reward);
        } else {
            g.streak = 0;
        }

        // Multiplayer pool bonus
        if (lootRarity < 5 && prizePool > 0) {
            uint256 bonus = prizePool / 10;
            prizePool -= bonus;
            reward += bonus;

            CasinoTreasury(payable(config.treasury))
                .payOut(token, g.player, bonus);
        }

        games[gameId] = GameResult({
            player: g.player,
            gameId: gameId,
            roll: roll,
            reward: reward,
            streak: g.streak,
            lootRarity: lootRarity
        });

        delete pendingGames[gameId];
        delete vrfRequestToGame[requestId];

        emit GameSettled(
            gameId,
            roll,
            reward,
            g.streak,
            lootRarity
        );
    }

    function getGame(uint256 gameId) external view returns (GameResult memory) {
        return games[gameId];
    }

    function getPendingGame(uint256 gameId) external view returns (PendingGame memory) {
        return pendingGames[gameId];
    }

    function getPrizePool() external view returns (uint256) {
        return prizePool;
    }

    function pause() external onlyOwner {
        config.paused = true;
    }

    function unpause() external onlyOwner {
        config.paused = false;
    }

    function setPythVRF(address _pythVRF) external onlyOwner {
        pythVRF = IPythVRF(_pythVRF);
    }
}

contracts/casino/Jackpot.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "../interfaces/IPythVRF.sol";
import "./libraries/CasinoMath.sol";

/**
 * @title Jackpot
 * @dev Mission X compliant, VRF-powered raffle/jackpot system
 * Features: VRF-determined winner selection, transparent prize distribution, arcade-style raffles
 */
contract Jackpot is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IPythVRF public pythVRF;
    address public token; // address(0) = native MON

    uint256 public totalPool;
    uint256 public totalEntries;
    uint16 public rakeBps; // 500 = 5%
    uint256 public raffleCounter;

    struct Entry {
        address player;
        uint256 amount;
        uint256 timestamp;
    }

    struct Raffle {
        uint256 raffleId;
        uint256 totalPool;
        uint256 totalEntries;
        address winner;
        bool drawn;
        bytes32 vrfRequestId;
    }

    mapping(uint256 => Entry[]) public entries; // raffleId => entries
    mapping(uint256 => Raffle) public raffles;
    mapping(bytes32 => uint256) public vrfRequestToRaffle;

    event EntryPlaced(
        uint256 indexed raffleId,
        address indexed player,
        uint256 amount,
        uint256 poolContribution
    );
    event WinnerDrawn(
        uint256 indexed raffleId,
        address indexed winner,
        uint256 prize
    );
    event RaffleCreated(uint256 indexed raffleId);

    constructor(
        address _owner,
        address _token,
        address _pythVRF
    ) Ownable(_owner) {
        token = _token;
        pythVRF = IPythVRF(_pythVRF);
        rakeBps = 500; // 5% default
    }

    function placeBet(uint256 amount) external payable nonReentrant {
        // Handle bet payment
        if (token == address(0)) {
            require(msg.value == amount, "Amount mismatch");
        } else {
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        }

        uint256 rake = (amount * rakeBps) / 10000;
        uint256 poolContribution = amount - rake;

        // Get or create current raffle
        uint256 currentRaffleId = raffleCounter;
        if (raffles[currentRaffleId].raffleId == 0) {
            raffles[currentRaffleId] = Raffle({
                raffleId: currentRaffleId,
                totalPool: 0,
                totalEntries: 0,
                winner: address(0),
                drawn: false,
                vrfRequestId: bytes32(0)
            });
            emit RaffleCreated(currentRaffleId);
        }

        Raffle storage raffle = raffles[currentRaffleId];
        require(!raffle.drawn, "Raffle already drawn");

        raffle.totalPool += poolContribution;
        raffle.totalEntries += 1;

        entries[currentRaffleId].push(Entry({
            player: msg.sender,
            amount: poolContribution,
            timestamp: block.timestamp
        }));

        totalPool += poolContribution;
        totalEntries += 1;

        emit EntryPlaced(currentRaffleId, msg.sender, amount, poolContribution);
    }

    function drawWinner(uint256 raffleId) external onlyOwner {
        Raffle storage raffle = raffles[raffleId];
        require(raffle.raffleId == raffleId, "Invalid raffle");
        require(!raffle.drawn, "Already drawn");
        require(raffle.totalPool > 0, "No pool");
        require(raffle.totalEntries > 0, "No entries");

        // Mission X: No seed - VRF generates its own entropy
        bytes32 requestId = pythVRF.requestRandomness();
        raffle.vrfRequestId = requestId;
        vrfRequestToRaffle[requestId] = raffleId;
    }

    /**
     * @dev Mission X: Push-based callback - VRF calls this with randomness
     */
    function fulfillRandomness(bytes32 requestId, uint256 randomness) external {
        require(msg.sender == address(pythVRF), "Only VRF");

        uint256 raffleId = vrfRequestToRaffle[requestId];
        Raffle storage raffle = raffles[raffleId];
        require(raffle.raffleId == raffleId, "Invalid raffle");
        require(!raffle.drawn, "Already drawn");

        Entry[] storage raffleEntries = entries[raffleId];
        require(raffleEntries.length > 0, "No entries");

        // Select winner using VRF
        uint256 winnerIndex = randomness % raffleEntries.length;
        address winner = raffleEntries[winnerIndex].player;

        uint256 prize = raffle.totalPool;
        raffle.winner = winner;
        raffle.drawn = true;

        totalPool -= prize;
        totalEntries -= raffle.totalEntries;

        // Payout prize
        if (token == address(0)) {
            (bool success, ) = payable(winner).call{value: prize}("");
            require(success, "Transfer failed");
        } else {
            IERC20(token).safeTransfer(winner, prize);
        }

        delete vrfRequestToRaffle[requestId];

        emit WinnerDrawn(raffleId, winner, prize);
    }

    function startNewRaffle() external onlyOwner {
        raffleCounter++;
    }

    function getRaffle(uint256 raffleId) external view returns (Raffle memory) {
        return raffles[raffleId];
    }

    function getRaffleEntries(uint256 raffleId) external view returns (Entry[] memory) {
        return entries[raffleId];
    }

    function setRake(uint16 newRakeBps) external onlyOwner {
        require(newRakeBps <= 1000, "Rake too high"); // Max 10%
        rakeBps = newRakeBps;
    }

    function setPythVRF(address _pythVRF) external onlyOwner {
        pythVRF = IPythVRF(_pythVRF);
    }
}

contracts/casino/libraries/CasinoMath.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

library CasinoMath {
    error InvalidBetAmount();
    error BetTooLow();
    error BetTooHigh();
    error MathOverflow();

    function calculatePayout(
        uint256 betAmount,
        uint256 multiplierBps,
        uint16 houseEdgeBps
    ) internal pure returns (uint256) {
        uint256 payout = (betAmount * multiplierBps) / 10000;
        uint256 houseEdge = (payout * houseEdgeBps) / 10000;
        return payout - houseEdge;
    }

    function validateBet(
        uint256 betAmount,
        uint256 minBet,
        uint256 maxBet
    ) internal pure {
        if (betAmount < minBet) revert BetTooLow();
        if (betAmount > maxBet) revert BetTooHigh();
    }

    function generateRandom(uint256 seed, uint256 max) internal view returns (uint256) {
        return uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, seed))) % (max + 1);
    }
}

