//SPDX-License-Identifier:MIT
pragma solidity ^0.8.24;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';

/**
 * @title MonFair
 * @dev VRF-powered memory card game on Monad blockchain
 * Features:
 * - Minimum bet: 1 MON
 * - 2-4 players (including AI)
 * - AI vs Player: AI wins 2x bet
 * - Player vs Player: Fastest player wins all
 * - 10% commission on player vs player games
 */
contract MonFair is Ownable, ReentrancyGuard {
    constructor() Ownable(msg.sender) {}
    // Constants
    uint256 public constant MIN_BET = 1 ether; // 1 MON
    uint256 public constant MAX_PLAYERS = 5;
    uint256 public constant MIN_PLAYERS = 1; // Allow single player mode
    uint256 public constant COMMISSION_PCT = 10; // 10% commission
    address public constant AI_ADDRESS = address(0x1111111111111111111111111111111111111111); // AI player address
    
    uint256 private _gameIdCounter;
    uint256 public treasuryBalance; // Accumulated treasury balance

    enum GameType {
        AI_VS_PLAYER,    // Player vs AI
        PLAYER_VS_PLAYER // Multiple players
    }

    enum GameStatus {
        CREATED,      // Game created, waiting for players
        IN_PROGRESS,  // Game started, players playing
        COMPLETED,    // Game finished, winner determined
        CANCELLED     // Game cancelled
    }

    struct Game {
        uint256 id;
        string name;             // Custom game name
        address creator;
        GameType gameType;
        GameStatus status;
        uint256 stake;           // Bet amount per player (in MON)
        uint256 totalPrize;       // Total prize pool
        uint256 maxPlayers;       // 2, 3, or 4
        uint256 currentPlayers;   // Current number of players
        uint256 createdAt;
        uint256 startedAt;
        uint256 completedAt;
        uint256 endTime;           // When the game will end (for multi-player games)
        address winner;           // Winner address (or AI_ADDRESS if AI wins)
        uint256 winnerFlipCount;  // Winner's flip count (lower is better)
        bytes32 vrfRequestId;    // VRF request ID for card shuffling
        uint256[] cardOrder;      // Card order determined by VRF
        bool vrfFulfilled;        // Whether VRF has been fulfilled
    }

    struct Player {
        address playerAddress;
        uint256 flipCount;        // Number of flips to complete game
        uint256 completedAt;      // Timestamp when player finished
        bool hasCompleted;        // Whether player has finished
        bool hasJoined;           // Whether player has joined
    }

    // Mappings
    mapping(uint256 => Game) public games;
    mapping(uint256 => mapping(address => Player)) public players; // gameId => playerAddress => Player
    mapping(uint256 => address[]) public gamePlayers; // gameId => array of player addresses
    mapping(address => uint256[]) public playerGames; // playerAddress => array of game IDs
    mapping(bytes32 => uint256) public vrfRequests; // vrfRequestId => gameId

    // Events
    event GameCreated(
        uint256 indexed gameId,
        string name,
        address indexed creator,
        GameType gameType,
        uint256 stake,
        uint256 maxPlayers
    );

    event PlayerJoined(
        uint256 indexed gameId,
        address indexed player,
        uint256 currentPlayers,
        uint256 maxPlayers
    );

    event GameStarted(
        uint256 indexed gameId,
        bytes32 indexed vrfRequestId
    );

    event VRFRequested(
        uint256 indexed gameId,
        bytes32 indexed vrfRequestId
    );

    event VRFFulfilled(
        uint256 indexed gameId,
        bytes32 indexed vrfRequestId,
        uint256[] cardOrder
    );

    event PlayerCompleted(
        uint256 indexed gameId,
        address indexed player,
        uint256 flipCount,
        uint256 completedAt
    );

    event GameCompleted(
        uint256 indexed gameId,
        address indexed winner,
        uint256 prize,
        uint256 flipCount
    );

    event PrizeDistributed(
        uint256 indexed gameId,
        address indexed winner,
        uint256 amount
    );

    /**
     * @dev Create a new game
     * @param _name Custom name for the game
     * @param _gameType Type of game (AI_VS_PLAYER or PLAYER_VS_PLAYER)
     * @param _maxPlayers Number of players (2, 3, or 4)
     */
    function createGame(
        string memory _name,
        GameType _gameType,
        uint256 _maxPlayers,
        uint256 _durationHours    // Duration in hours (0 = no time limit for single player)
    ) external payable nonReentrant {
        require(msg.value >= MIN_BET, "Bet must be at least 1 MON");
        require(
            _maxPlayers >= MIN_PLAYERS && _maxPlayers <= MAX_PLAYERS,
            "Players must be between 1 and 5"
        );

        if (_gameType == GameType.AI_VS_PLAYER) {
            require(_maxPlayers == 1 || _maxPlayers == 2, "AI games must have 1 or 2 players");
        }

        _gameIdCounter++;
        uint256 gameId = _gameIdCounter;

        Game storage game = games[gameId];
        game.id = gameId;
        game.name = _name;
        game.creator = msg.sender;
        game.gameType = _gameType;
        game.status = GameStatus.CREATED;
        game.stake = msg.value;
        game.totalPrize = msg.value;
        game.maxPlayers = _maxPlayers;
        game.currentPlayers = 1;
        game.createdAt = block.timestamp;
        
        // Set end time for multi-player games (if duration is specified)
        if (_gameType == GameType.PLAYER_VS_PLAYER && _durationHours > 0) {
            game.endTime = block.timestamp + (_durationHours * 1 hours);
        } else {
            game.endTime = 0; // No time limit for single player or unlimited games
        }

        // Add creator as first player
        players[gameId][msg.sender] = Player({
            playerAddress: msg.sender,
            flipCount: 0,
            completedAt: 0,
            hasCompleted: false,
            hasJoined: true
        });
        gamePlayers[gameId].push(msg.sender);
        playerGames[msg.sender].push(gameId);

        // For single player AI games, add AI immediately and start game
        if (_gameType == GameType.AI_VS_PLAYER && _maxPlayers == 1) {
            players[gameId][AI_ADDRESS] = Player({
                playerAddress: AI_ADDRESS,
                flipCount: 0,
                completedAt: 0,
                hasCompleted: false,
                hasJoined: true
            });
            gamePlayers[gameId].push(AI_ADDRESS);
            game.currentPlayers = 2; // Creator + AI
            game.totalPrize += game.stake; // AI adds to prize pool
            
            // Auto-start single player game
            _startGame(gameId);
        }

        emit GameCreated(gameId, _name, msg.sender, _gameType, msg.value, _maxPlayers);
    }

    /**
     * @dev Join an existing game
     * @param _gameId Game ID to join
     */
    function joinGame(uint256 _gameId) external payable nonReentrant {
        Game storage game = games[_gameId];
        require(game.status == GameStatus.CREATED, "Game not available");
        require(game.currentPlayers < game.maxPlayers, "Game is full");
        require(msg.value >= game.stake, "Insufficient bet amount");
        require(!players[_gameId][msg.sender].hasJoined, "Already joined");

        // Add AI player if needed
        if (game.gameType == GameType.AI_VS_PLAYER && game.currentPlayers == 1) {
            players[_gameId][AI_ADDRESS] = Player({
                playerAddress: AI_ADDRESS,
                flipCount: 0,
                completedAt: 0,
                hasCompleted: false,
                hasJoined: true
            });
            gamePlayers[_gameId].push(AI_ADDRESS);
            game.currentPlayers++;
            game.totalPrize += game.stake; // AI doesn't pay, but prize pool increases
        }

        // Add human player
        players[_gameId][msg.sender] = Player({
            playerAddress: msg.sender,
            flipCount: 0,
            completedAt: 0,
            hasCompleted: false,
            hasJoined: true
        });
        gamePlayers[_gameId].push(msg.sender);
        playerGames[msg.sender].push(_gameId);
        game.currentPlayers++;
        game.totalPrize += msg.value;

        emit PlayerJoined(_gameId, msg.sender, game.currentPlayers, game.maxPlayers);

        // Auto-start game if full
        if (game.currentPlayers >= game.maxPlayers) {
            _startGame(_gameId);
        }
    }

    /**
     * @dev Start the game and request VRF for card shuffling
     * @param _gameId Game ID to start
     */
    function _startGame(uint256 _gameId) internal {
        Game storage game = games[_gameId];
        require(game.status == GameStatus.CREATED, "Game cannot be started");
        require(game.currentPlayers >= game.maxPlayers, "Not enough players");

        game.status = GameStatus.IN_PROGRESS;
        game.startedAt = block.timestamp;

        // Request VRF for card shuffling
        bytes32 vrfRequestId = _requestVRF(_gameId);
        game.vrfRequestId = vrfRequestId;
        vrfRequests[vrfRequestId] = _gameId;

        emit GameStarted(_gameId, vrfRequestId);
        emit VRFRequested(_gameId, vrfRequestId);

        // Automatically fulfill VRF using block.prevrandao for randomness
        _autoFulfillVRF(_gameId, vrfRequestId);
    }

    /**
     * @dev Request VRF for card shuffling
     * @param _gameId Game ID
     * @return requestId VRF request ID
     */
    function _requestVRF(uint256 _gameId) internal view returns (bytes32 requestId) {
        // TODO: Implement actual VRF call to Pyth or Switchboard
        // Placeholder: Generate a request ID
        // In production, this would call the VRF provider
        requestId = keccak256(abi.encodePacked(_gameId, block.timestamp, block.prevrandao));
        return requestId;
    }

    /**
     * @dev Automatically fulfill VRF using block.prevrandao
     * @param _gameId Game ID
     * @param _vrfRequestId VRF request ID
     */
    function _autoFulfillVRF(uint256 _gameId, bytes32 _vrfRequestId) internal {
        Game storage game = games[_gameId];
        require(!game.vrfFulfilled, "VRF already fulfilled");

        // Generate random words from block.prevrandao for verifiable randomness
        uint256[] memory randomWords = new uint256[](1);
        randomWords[0] = uint256(keccak256(abi.encodePacked(_gameId, block.prevrandao, block.timestamp, block.number)));

        // Generate card order from random words
        // 12 cards total (6 pairs)
        uint256[] memory cardOrder = new uint256[](12);
        for (uint256 i = 0; i < 12; i++) {
            cardOrder[i] = i;
        }

        // Shuffle using random words (Fisher-Yates shuffle)
        for (uint256 i = 0; i < 12; i++) {
            uint256 j = i + (randomWords[0] % (12 - i));
            // Use additional randomness for better distribution
            if (i > 0) {
                randomWords[0] = uint256(keccak256(abi.encodePacked(randomWords[0], i, j)));
            }
            (cardOrder[i], cardOrder[j]) = (cardOrder[j], cardOrder[i]);
        }

        game.cardOrder = cardOrder;
        game.vrfFulfilled = true;

        emit VRFFulfilled(_gameId, _vrfRequestId, cardOrder);
    }

    /**
     * @dev Fulfill VRF request and set card order (manual override for owner)
     * @param _vrfRequestId VRF request ID
     * @param _randomWords Random words from VRF
     */
    function fulfillVRF(
        bytes32 _vrfRequestId,
        uint256[] memory _randomWords
    ) external onlyOwner {
        uint256 gameId = vrfRequests[_vrfRequestId];
        require(gameId > 0, "Invalid VRF request");
        
        Game storage game = games[gameId];
        require(!game.vrfFulfilled, "VRF already fulfilled");

        // Generate card order from random words
        // 12 cards total (6 pairs)
        uint256[] memory cardOrder = new uint256[](12);
        for (uint256 i = 0; i < 12; i++) {
            cardOrder[i] = i;
        }

        // Shuffle using VRF random words
        for (uint256 i = 0; i < 12; i++) {
            uint256 j = i + (_randomWords[0] % (12 - i));
            (cardOrder[i], cardOrder[j]) = (cardOrder[j], cardOrder[i]);
        }

        game.cardOrder = cardOrder;
        game.vrfFulfilled = true;

        emit VRFFulfilled(gameId, _vrfRequestId, cardOrder);
    }

    /**
     * @dev Submit player's completion (called when player finishes the game)
     * @param _gameId Game ID
     * @param _flipCount Number of flips player took to complete
     */
    function submitCompletion(
        uint256 _gameId,
        uint256 _flipCount
    ) external nonReentrant {
        Game storage game = games[_gameId];
        require(game.status == GameStatus.IN_PROGRESS, "Game not in progress");
        require(game.vrfFulfilled, "VRF not fulfilled yet");
        require(players[_gameId][msg.sender].hasJoined, "Not a player");
        require(!players[_gameId][msg.sender].hasCompleted, "Already completed");

        Player storage player = players[_gameId][msg.sender];
        player.flipCount = _flipCount;
        player.completedAt = block.timestamp;
        player.hasCompleted = true;

        emit PlayerCompleted(_gameId, msg.sender, _flipCount, block.timestamp);

        // For AI vs Player games, automatically submit AI completion
        if (game.gameType == GameType.AI_VS_PLAYER && 
            players[_gameId][AI_ADDRESS].hasJoined && 
            !players[_gameId][AI_ADDRESS].hasCompleted) {
            // Generate AI flip count: random between player's flip count and player's flip count + 10
            // This gives AI a chance to win or lose
            uint256 randomSeed = uint256(keccak256(abi.encodePacked(
                _gameId,
                block.prevrandao,
                block.timestamp,
                _flipCount
            )));
            uint256 aiFlipCount = _flipCount + (randomSeed % 11); // 0-10 range
            
            Player storage aiPlayer = players[_gameId][AI_ADDRESS];
            aiPlayer.flipCount = aiFlipCount;
            aiPlayer.completedAt = block.timestamp;
            aiPlayer.hasCompleted = true;

            emit PlayerCompleted(_gameId, AI_ADDRESS, aiFlipCount, block.timestamp);
        }

        // Check if all players have completed
        _checkGameCompletion(_gameId);
    }

    /**
     * @dev Submit AI completion (called by owner/oracle)
     * @param _gameId Game ID
     * @param _flipCount AI's flip count
     */
    function submitAICompletion(
        uint256 _gameId,
        uint256 _flipCount
    ) external onlyOwner {
        Game storage game = games[_gameId];
        require(game.status == GameStatus.IN_PROGRESS, "Game not in progress");
        require(game.gameType == GameType.AI_VS_PLAYER, "Not an AI game");
        require(players[_gameId][AI_ADDRESS].hasJoined, "AI not in game");
        require(!players[_gameId][AI_ADDRESS].hasCompleted, "AI already completed");

        Player storage aiPlayer = players[_gameId][AI_ADDRESS];
        aiPlayer.flipCount = _flipCount;
        aiPlayer.completedAt = block.timestamp;
        aiPlayer.hasCompleted = true;

        emit PlayerCompleted(_gameId, AI_ADDRESS, _flipCount, block.timestamp);

        _checkGameCompletion(_gameId);
    }

    /**
     * @dev Check if game is complete and determine winner
     * @param _gameId Game ID
     */
    function _checkGameCompletion(uint256 _gameId) internal {
        Game storage game = games[_gameId];
        
        // Check if game time has expired (for multi-player games)
        if (game.endTime > 0 && block.timestamp >= game.endTime && game.status == GameStatus.IN_PROGRESS) {
            // Time expired, determine winner based on current scores
            _determineWinner(_gameId);
            return;
        }
        
        // Check if all players have completed
        bool allCompleted = true;
        for (uint256 i = 0; i < gamePlayers[_gameId].length; i++) {
            address playerAddr = gamePlayers[_gameId][i];
            if (!players[_gameId][playerAddr].hasCompleted) {
                allCompleted = false;
                break;
            }
        }

        if (allCompleted) {
            _determineWinner(_gameId);
        }
    }

    /**
     * @dev Determine winner and distribute prizes
     * @param _gameId Game ID
     */
    function _determineWinner(uint256 _gameId) internal {
        Game storage game = games[_gameId];
        require(game.status == GameStatus.IN_PROGRESS, "Game not in progress");

        address winner = address(0);
        uint256 lowestFlipCount = type(uint256).max;

        // For AI vs Player games, use 49% chance for player to win
        if (game.gameType == GameType.AI_VS_PLAYER) {
            // Generate random number using block.prevrandao and game data
            uint256 randomSeed = uint256(keccak256(abi.encodePacked(
                _gameId,
                block.prevrandao,
                block.timestamp,
                gamePlayers[_gameId].length
            )));
            
            // 49% chance for player (0-48 wins), 51% chance for AI (49-99 wins)
            uint256 randomValue = randomSeed % 100;
            
            address playerAddress = address(0);
            uint256 playerFlipCount = type(uint256).max;
            
            // Find the human player (not AI)
            for (uint256 i = 0; i < gamePlayers[_gameId].length; i++) {
                address playerAddr = gamePlayers[_gameId][i];
                if (playerAddr != AI_ADDRESS) {
                    playerAddress = playerAddr;
                    playerFlipCount = players[_gameId][playerAddr].flipCount;
                    break;
                }
            }
            
            // 49% chance: Player wins if randomValue < 49
            if (randomValue < 49 && playerAddress != address(0)) {
                winner = playerAddress;
                lowestFlipCount = playerFlipCount;
            } else {
                // 51% chance: AI wins
                winner = AI_ADDRESS;
                lowestFlipCount = players[_gameId][AI_ADDRESS].flipCount;
            }
        } else {
            // Player vs Player: Find player with lowest flip count
            // If time expired, only consider players who have completed
            bool timeExpired = game.endTime > 0 && block.timestamp >= game.endTime;
            
            for (uint256 i = 0; i < gamePlayers[_gameId].length; i++) {
                address playerAddr = gamePlayers[_gameId][i];
                Player storage player = players[_gameId][playerAddr];
                
                // If game time expired, only consider completed players
                if (timeExpired && !player.hasCompleted) {
                    continue; // Skip players who haven't completed
                }
                
                uint256 flipCount = player.flipCount;
                // Only consider players who have completed (flipCount > 0)
                if (flipCount > 0 && flipCount < lowestFlipCount) {
                    lowestFlipCount = flipCount;
                    winner = playerAddr;
                }
            }
        }

        require(winner != address(0), "No winner found");

        game.winner = winner;
        game.winnerFlipCount = lowestFlipCount;
        game.status = GameStatus.COMPLETED;
        game.completedAt = block.timestamp;

        emit GameCompleted(_gameId, winner, game.totalPrize, lowestFlipCount);

        // Distribute prize
        _distributePrize(_gameId, winner);
    }

    /**
     * @dev Distribute prize to winner
     * @param _gameId Game ID
     * @param _winner Winner address
     */
    function _distributePrize(uint256 _gameId, address _winner) internal {
        Game storage game = games[_gameId];
        uint256 prize = game.totalPrize;

        if (game.gameType == GameType.AI_VS_PLAYER) {
            if (_winner == AI_ADDRESS) {
                // AI wins: gets 2x of player's bet
                uint256 playerBet = game.stake;
                uint256 aiPrize = playerBet * 2;
                prize = aiPrize;
                
                // Accumulate in treasury instead of paying immediately
                treasuryBalance += prize;
            } else {
                // Player wins: gets their bet back (no 2x for player)
                prize = game.stake;
                payTo(_winner, prize);
            }
        } else {
            // Player vs Player
            uint256 commission = (prize * COMMISSION_PCT) / 100;
            uint256 winnerPrize = prize - commission;

            // Pay winner
            payTo(_winner, winnerPrize);

            // Accumulate commission in treasury
            if (commission > 0) {
                treasuryBalance += commission;
            }
        }

        emit PrizeDistributed(_gameId, _winner, prize);
    }

    /**
     * @dev Withdraw accumulated treasury balance (only owner)
     */
    function withdrawTreasury() external onlyOwner nonReentrant {
        uint256 amount = treasuryBalance;
        require(amount > 0, "No funds to withdraw");
        
        treasuryBalance = 0;
        payTo(owner(), amount);
    }

    /**
     * @dev Cancel game and refund players
     * @param _gameId Game ID
     */
    function cancelGame(uint256 _gameId) external {
        Game storage game = games[_gameId];
        require(game.creator == msg.sender || owner() == msg.sender, "Not authorized");
        require(game.status == GameStatus.CREATED, "Game already started");

        game.status = GameStatus.CANCELLED;

        // Refund all players
        for (uint256 i = 0; i < gamePlayers[_gameId].length; i++) {
            address playerAddr = gamePlayers[_gameId][i];
            if (playerAddr != AI_ADDRESS) {
                payTo(playerAddr, game.stake);
            }
        }
    }

    /**
     * @dev Internal function to send payments
     */
    function payTo(address to, uint256 amount) internal {
        (bool success, ) = payable(to).call{value: amount}("");
        require(success, "Payment failed");
    }

    // View functions

    /**
     * @dev Get game details
     */
    function getGame(uint256 _gameId) external view returns (Game memory) {
        return games[_gameId];
    }

    /**
     * @dev Get player details for a game
     */
    function getPlayer(uint256 _gameId, address _player) external view returns (Player memory) {
        return players[_gameId][_player];
    }

    /**
     * @dev Get all players in a game
     */
    function getGamePlayers(uint256 _gameId) external view returns (address[] memory) {
        return gamePlayers[_gameId];
    }

    /**
     * @dev Get all games for a player
     */
    function getPlayerGames(address _player) external view returns (uint256[] memory) {
        return playerGames[_player];
    }

    /**
     * @dev Get active games (created or in progress)
     */
    function getActiveGames() external view returns (Game[] memory) {
        uint256 count = 0;
        for (uint256 i = 1; i <= _gameIdCounter; i++) {
            if (games[i].status == GameStatus.CREATED || games[i].status == GameStatus.IN_PROGRESS) {
                count++;
            }
        }

        Game[] memory activeGames = new Game[](count);
        uint256 index = 0;
        for (uint256 i = 1; i <= _gameIdCounter; i++) {
            if (games[i].status == GameStatus.CREATED || games[i].status == GameStatus.IN_PROGRESS) {
                activeGames[index] = games[i];
                index++;
            }
        }
        return activeGames;
    }

    /**
     * @dev Get card order for a game (after VRF fulfillment)
     */
    function getCardOrder(uint256 _gameId) external view returns (uint256[] memory) {
        require(games[_gameId].vrfFulfilled, "VRF not fulfilled");
        return games[_gameId].cardOrder;
    }

    // Owner functions

    /**
     * @dev Withdraw contract balance (emergency only)
     */
    function withdraw() external onlyOwner {
        payTo(owner(), address(this).balance);
    }

    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {}
}

