contracts/casino/CasinoTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract CasinoTreasury is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    mapping(address => bool) public authorizedGames;
    bool public paused;

    event FundsDeposited(address indexed token, uint256 amount);
    event FundsWithdrawn(address indexed token, uint256 amount, address to);
    event GameAuthorized(address indexed game);
    event GameRevoked(address indexed game);
    event Paused();
    event Unpaused();

    modifier onlyAuthorized() {
        require(authorizedGames[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Treasury paused");
        _;
    }

    constructor(address _owner) Ownable(_owner) {
        paused = false;
    }

    function authorizeGame(address game) external onlyOwner {
        authorizedGames[game] = true;
        emit GameAuthorized(game);
    }

    function revokeGame(address game) external onlyOwner {
        authorizedGames[game] = false;
        emit GameRevoked(game);
    }

    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }

    function unpause() external onlyOwner {
        paused = false;
        emit Unpaused();
    }

    function deposit(address token, uint256 amount) external payable whenNotPaused {
        if (token == address(0)) {
            // Native MON
            require(msg.value == amount, "Amount mismatch");
            emit FundsDeposited(address(0), amount);
        } else {
            // ERC20 token
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
            emit FundsDeposited(token, amount);
        }
    }

    function withdraw(address token, uint256 amount, address to) external onlyOwner nonReentrant {
        require(to != address(0), "Invalid recipient");
        if (token == address(0)) {
            // Native MON
            (bool success, ) = payable(to).call{value: amount}("");
            require(success, "Transfer failed");
            emit FundsWithdrawn(address(0), amount, to);
        } else {
            // ERC20 token
            IERC20(token).safeTransfer(to, amount);
            emit FundsWithdrawn(token, amount, to);
        }
    }

    function transferToGame(address token, address game, uint256 amount) external onlyAuthorized whenNotPaused {
        if (token == address(0)) {
            // Native MON
            (bool success, ) = payable(game).call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            // ERC20 token
            IERC20(token).safeTransfer(game, amount);
        }
    }

    function payOut(address token, address recipient, uint256 amount) external onlyAuthorized whenNotPaused {
        require(recipient != address(0), "Invalid recipient");
        if (token == address(0)) {
            // Native MON
            require(address(this).balance >= amount, "Insufficient treasury balance");
            (bool success, ) = payable(recipient).call{value: amount}("");
            require(success, "Payout failed");
        } else {
            // ERC20 token
            IERC20(token).safeTransfer(recipient, amount);
        }
    }

    function getBalance(address token) external view returns (uint256) {
        if (token == address(0)) {
            // Native MON
            return address(this).balance;
        } else {
            // ERC20 token
            return IERC20(token).balanceOf(address(this));
        }
    }

    // Allow contract to receive native MON
    receive() external payable {
        emit FundsDeposited(address(0), msg.value);
    }
}

contracts/casino/CoinFlip.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/ICasinoGame.sol";
import "./libraries/CasinoMath.sol";
import "./CasinoTreasury.sol";

contract CoinFlip is ICasinoGame, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    GameConfig public config;
    mapping(uint256 => GameState) public games;
    uint256 public gameCounter;
    address public token; // Can be address(0) for native MON

    event GamePlayed(
        address indexed player,
        uint256 indexed gameId,
        uint256 betAmount,
        uint256 choice,
        uint256 result,
        uint256 payout
    );

    constructor(address _owner, address _token) Ownable(_owner) {
        token = _token;
    }

    function initialize(
        address treasury,
        uint256 minBet,
        uint256 maxBet,
        uint16 houseEdgeBps
    ) external onlyOwner {
        config = GameConfig({
            treasury: treasury,
            minBet: minBet,
            maxBet: maxBet,
            houseEdgeBps: houseEdgeBps,
            paused: false
        });
    }

    function play(uint256 betAmount, uint8 choice) external payable nonReentrant {
        require(!config.paused, "Game paused");
        require(choice <= 1, "Invalid choice");
        CasinoMath.validateBet(betAmount, config.minBet, config.maxBet);

        // Handle bet payment
        if (token == address(0)) {
            // Native MON
            require(msg.value == betAmount, "Amount mismatch");
            (bool success, ) = payable(config.treasury).call{value: betAmount}("");
            require(success, "Transfer failed");
        } else {
            // ERC20 token
            IERC20(token).safeTransferFrom(msg.sender, config.treasury, betAmount);
        }

        uint256 seed = uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender)));
        uint256 result = CasinoMath.generateRandom(seed, 1);

        uint256 gameId = ++gameCounter;
        bool won = (choice == result);
        uint256 payout = 0;

        if (won) {
            // 1.95x payout = 19500 bps
            payout = CasinoMath.calculatePayout(betAmount, 19500, config.houseEdgeBps);

            // Payout from treasury
            CasinoTreasury treasury = CasinoTreasury(payable(config.treasury));
            treasury.payOut(token, msg.sender, payout);
        }

        games[gameId] = GameState({
            player: msg.sender,
            betAmount: betAmount,
            gameId: gameId,
            timestamp: block.timestamp,
            settled: true,
            result: result,
            payout: payout
        });

        emit GamePlayed(msg.sender, gameId, betAmount, choice, result, payout);
    }

    function pause() external onlyOwner {
        config.paused = true;
    }

    function unpause() external onlyOwner {
        config.paused = false;
    }
}

